				tabMenu.click(function(){
					var tabMenuActive = $(this).attr('tab');
					var tabContentActive = $(".content .block."+tabMenuActive);

					tabMenu.removeClass('active');
					$(this).addClass('active');

					tabContent.addClass('hide');
					tabContentActive.removeClass('hide');
				});
			});

